if (!empty($_POST['action']) && $_POST['action'] == 'load_product') {
    $category = $_POST['category'];
    $sql = "SELECT * FROM `nv4_shops_rows` WHERE FIND_IN_SET($category, listcatid)";
    $products = $db->query($sql)->fetchAll();
    $response = [
        'success' => 0,
        'data' => []
    ];

    if (empty($products)) {
        header('Content-Type: application/json');
        echo json_encode($response);
        exit;
    }

    $html = '';

    foreach ($products as $product) {
        $html .= '
            <div class="product">
                <img src="/uploads/shops/'.$product['homeimgfile'].'" alt="'.$product['en_title'].'">
                <div class="info">
                    <p class="title">'.$product['en_title'].'</p>
                    <p class="price">$'.$product['product_price'].'</p>
                </div>
            </div>
        ';
    }

    $response['success'] = 1;
    $response['data'] = $html;

    header('Content-Type: application/json');
    echo json_encode($response);
    exit;
}

