function crawlProducts($node, $xpath) {
    $products = [];

    foreach ($node->childNodes as $li) {
        if ($li->nodeName === 'li') {
            $product = [];
            $img = $li->getElementsByTagName('img')->item(0);
            $product['name'] = $img->getAttribute('alt');
            $product['link'] = $img->getAttribute('src');

            $price = $xpath->query(".//*[contains(concat(' ', normalize-space(@class), ' '), ' price--main ')]", $li)->item(0);
            preg_match('/[\d.]+/', $price->nodeValue, $matches);
            $product['price'] = $matches[0];
            if (!empty($product)) $products[] = $product;
        }
    }

    return $products;
}

function crawlCategories($node, $level = 0) {
    $categories = [];

    // Duyệt qua tất cả các <li> của node hiện tại
    foreach ($node->childNodes as $li) {
        if ($li->nodeName === 'li') {
            $category = [];

            // Tìm thẻ <a> chứa tên category
            $a = $li->getElementsByTagName('a')->item(0);
            if ($a) {
                $categoryName = trim($a->nodeValue);
                $categoryLink = $a->getAttribute('href');

                // Lưu category
                $category['name'] = $categoryName;
                $category['link'] = $categoryLink;
            }

            // Tìm danh mục con (nếu có)
            $subUl = $li->getElementsByTagName('ul')->item(0);
            if ($subUl) $category['children'] = crawlCategories($subUl, $level + 1);
            if (!empty($category)) $categories[] = $category;
        }
    }

    return $categories;
}

function insertProduct($data, $cat) {
    global $db;

    $name = nv_htmlspecialchars($data['name']);
    $alias = strtolower(change_alias($data['name']));
    $imageContent = file_get_contents($data['link']);
    $imagePath = "uploads/products/crawl/$alias.jpg";
    if (!file_exists($imagePath)) file_put_contents($imagePath, $imageContent);
    $imageUrl = '/'.$imagePath;
    $desc = '<p>Images are for illustrative purposes only.</p><p>The actual product may differ from the picture shown.</p>';
    
    $columns = [
        'listcatid' => $cat,
        'user_id' => 4,
        'addtime' => time(),
        'edittime' => time(),
        'publtime' => time(),
        'archive' => 2,
        'product_price' => $data['price'],
        'money_unit' => 'USD',
        'product_unit' => 1,
        'homeimgfile' => $imageUrl,
        'homeimgthumb' => 3,
        'inhome' => 1,
        'showprice' => 1,
        'en_title' => $name,
        'en_alias' => $alias,
        'en_hometext' => $desc,
        'en_bodytext' => $desc,
    ];

    $exist = $db->query("SELECT `id`, `listcatid` FROM `nv4_shops_rows` WHERE `en_title` = '".$name."'")->fetch();

    if (empty(!$exist)) {
        $listCatId = explode(',', $exist['listcatid']);

        if (!in_array($cat, $listCatId)) {
            $listCatId[] = $cat;
            $sql = "UPDATE `nv4_shops_rows` SET `listcatid` = '".implode(',', $listCatId)."' WHERE `id` = ".$exist['id'];
            $db->query($sql);
        }
    }
    else {
        $sql = "INSERT INTO `nv4_shops_rows` (`".implode("`,`", array_keys($columns))."`) VALUES ('".implode("','", $columns)."')";
        $db->query($sql);
        // $lastId = $db->lastInsertId();

