                                                    ';
                                                }
                $categoryContent .= '       </ul>
                                        </li>
                                    ';
                                }
                                else {
                                    $categoryContent .= '                
                                        <li class="item level-1"><a class="item-action level-1" href="/'.$childCat['en_alias'].'.html" data-cat="'.$childCat['catid'].'">'.$childCat['en_title'].'</a></li>
                                    ';
                                }
                            }
            $categoryContent .= '
                        </ul>
                    </div>
                </li>
            ';
        }
        else {
            $categoryContent .= '
                <li class="item">
                    <a class="item-action" href="/'.$cat['en_alias'].'.html" data-cat="'.$cat['catid'].'">'.$cat['en_title'].'</a>
                </li>
            ';
        }
        
    }

    $contents = nv_page_main($categoryContent, $array_tag, $other_links, $content_comment);

